package sinhntpk00491_lab6;

import android.app.Activity;
import android.os.Bundle;
import android.widget.TextView;
import screenoption.com.R;

public class lab6_intent extends Activity{
	TextView name,address,weight;
	@Override
	protected void onCreate(Bundle savedInstanceState) {
		// TODO Auto-generated method stub
		super.onCreate(savedInstanceState);
		setContentView(R.layout.lab6_intent);
		name=(TextView) findViewById(R.id.txtname);
		address=(TextView) findViewById(R.id.txtaddress);
		weight=(TextView) findViewById(R.id.txtweight);
		String Gift1 = getIntent().getExtras().getString("gift1");
		String Gift2 = getIntent().getExtras().getString("gift2");
		String Gift3 = getIntent().getExtras().getString("gift3");
		name.setText(Gift1);
		address.setText(Gift2);
		weight.setText(Gift3);
	}
}











































































































































































































































































































